"use strict";function showLoader(){loader.style.display="block"}function hideLoader(){loader.style.display="none"}async function getData(){const t=input.value;try{const e=await fetch(`https://itunes.apple.com/search?term=${t}&media=music`,{method:"GET"}),n=await e.json();0===n.resultCount?(hideLoader(),poruka.innerText="Nažalost nema rezultata. Provjerite unos i pokušajte ponovo."):(hideLoader(),showResults(n.results))}catch(t){poruka.innerText="Došlo je do pogreške. Ponovite unos i pokušajte ponovo."}}function showResults(t){t.forEach(t=>{const e=document.createElement("li");return e.textContent=t.artistName+" - "+t.trackName,lista.appendChild(e),e})}const input=document.getElementById("input"),lista=document.getElementById("lista"),loader=document.getElementById("loader"),poruka=document.getElementById("poruka"),button=document.getElementById("search-button");input.addEventListener("click",function(){input.value="",lista.innerText="",poruka.innerText=""}),button.addEventListener("click",function(){showLoader(),getData()});