"use strict";async function search(e){if(showLoader(),""===e)return hideLoader(),void showMessage("Unesite podatke za pretraživanje.");try{var t=await fetchData(e);hideLoader(),0===t.resultCount?showMessage("Nema rezultata."):displayResults(t.results)}catch(e){hideLoader(),showMessage("Došlo je do pogreške tijekom pretraživanja.")}}async function fetchData(e){const t=`https://itunes.apple.com/search?term=${encodeURIComponent(e)}&entity=song`;var a=await fetch(t),s=await a.json();return s}function displayResults(e){e.forEach(e=>{var t=document.createElement("tr"),a=document.createElement("td"),s=document.createElement("td");a.textContent=e.trackName,s.textContent=e.artistName,t.appendChild(a),t.appendChild(s),resultsList.appendChild(t)})}function showLoader(){resultsList.innerHTML="",message.textContent="",loader.style.display="block"}function hideLoader(){loader.style.display="none"}function showMessage(e){message.textContent=e}const searchInput=document.getElementById("search-input"),resultsList=document.getElementById("results-list"),message=document.getElementById("message"),loader=document.getElementById("loader");searchInput.addEventListener("input",e=>{const t=e.target.value;search(t)});