async function searchITunes(e){const t=`https://itunes.apple.com/search?term=${e}&entity=song`;try{const e=await fetch(t);if(e.ok){const t=await e.json();displayResults(t.results)}else displayError("Došlo je do pogreške prilikom dohvaćanja podataka.")}catch(e){displayError("Došlo je do pogreške prilikom dohvaćanja podataka.")}}function displayResults(e){const t=document.getElementById("results-table");if(t.innerHTML="",0!==e.length)for(const n of e){const e=t.insertRow(),a=e.insertCell(),s=e.insertCell();a.innerHTML=n.trackName,s.innerHTML=n.artistName}else displayError("Nema rezultata za prikaz.")}function displayError(e){const t=document.getElementById("results-table");t.innerHTML="";const n=t.insertRow(),a=n.insertCell();a.colSpan=2,a.innerHTML=e}function handleInputChange(){const e=document.getElementById("search-input"),t=e.value.trim();""!==t?searchITunes(t):displayError("Unesite traženi termin.")}const input=document.getElementById("search-input");input.addEventListener("input",handleInputChange);