async function fetchData(t){const e=`https://itunes.apple.com/search?term=${t}&entity=song`;let n=await fetch(e),a=await n.json();return a}function displayResults(t){t.forEach(t=>{let e=document.createElement("tr"),n=document.createElement("td"),a=document.createElement("td");n.textContent=t.trackName,a.textContent=t.artistName,e.appendChild(n),e.appendChild(a),resultsTable.appendChild(e)})}function showLoad(){resultsTable.innerHTML="",text.textContent="",load.style.display="block"}function hideLoad(){load.style.display="none"}function showMessage(t){text.textContent=t}async function search(t){showLoad();try{var e=await fetchData(t);if(hideLoad(),0===e.resultCount)return;displayResults(e.results)}catch(t){hideLoad()}}const songSearch=document.getElementById("song-search"),resultsTable=document.getElementById("results-table"),text=document.getElementById("text"),load=document.getElementById("load");songSearch.addEventListener("input",t=>{const e=t.target.value;search(e)});